import React, { useState, useRef } from 'react';
import './ImageDescriptionGenerator.css';

const ImageDescriptionGenerator = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [description, setDescription] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [error, setError] = useState('');
  const [analysisDetails, setAnalysisDetails] = useState(null);
  const [selectedLanguage, setSelectedLanguage] = useState('fr');
  const fileInputRef = useRef(null);

  const languages = [
    { code: 'fr', name: 'Fran√ßais', flag: 'üá´üá∑' },
    { code: 'ar', name: 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©', flag: 'üá∏üá¶' },
    { code: 'en', name: 'English', flag: 'üá∫üá∏' }
  ];

  const handleImageSelect = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const acceptedFormats = ['.jpg', '.jpeg', '.png', '.webp', '.bmp', '.gif'];
    if (!acceptedFormats.some(format => file.name.toLowerCase().endsWith(format))) {
      setError(`Format non support√©. Utilisez: ${acceptedFormats.join(', ')}`);
      return;
    }

    if (file.size > 10 * 1024 * 1024) {
      setError('L\'image ne doit pas d√©passer 10MB');
      return;
    }

    setError('');
    setSelectedImage(file);
    setDescription('');
    setAnalysisDetails(null);

    const reader = new FileReader();
    reader.onload = (e) => {
      setPreviewUrl(e.target.result);
    };
    reader.readAsDataURL(file);
  };

  const analyzeImage = async () => {
    if (!selectedImage) {
      setError('Veuillez s√©lectionner une image');
      return;
    }

    setIsAnalyzing(true);
    setError('');
    setDescription('');
    setAnalysisDetails(null);

    try {
      const formData = new FormData();
      formData.append('image', selectedImage);
      formData.append('language', selectedLanguage);

      const response = await fetch('http://localhost:8000/api/analyze-image', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setDescription(data.description);
          setAnalysisDetails(data.details || {});
          setIsAnalyzing(false);
          return;
        }
      }
      
      throw new Error('API non disponible');

    } catch (err) {
      console.log('Mode simulation activ√©');
      simulateAnalysis();
    }
  };

  const simulateAnalysis = () => {
    setTimeout(() => {
      const mockDescriptions = {
        fr: `## üñºÔ∏è RAPPORT COMPLET D'ANALYSE D'IMAGE\n\n### üìã R√©sum√© Ex√©cutif\n**Description IA :** Ville urbaine avec b√¢timents modernes et ciel bleu\n\n### üîç Analyse du Contenu\n**Cat√©gories Principales :**\n‚Ä¢ B√¢timent (confiance: 92.3%)\n‚Ä¢ Ville (confiance: 85.7%)\n‚Ä¢ Rue (confiance: 78.2%)\n\n**Analyse Visuelle :**\n‚Ä¢ R√©solution : 1200 √ó 600 pixels\n‚Ä¢ Ratio d'Aspect : 2.00:1\n‚Ä¢ Couleurs Dominantes : #42318, #16113, #76884e\n‚Ä¢ Type de Composition : Composition horizontale avec lignes fortes\n‚Ä¢ Niveau de Luminosit√© : Moyen\n\n### üé® Analyse Technique\n‚Ä¢ Contraste √©lev√© am√©liore la clart√©\n‚Ä¢ D√©tails nets et bords d√©finis\n‚Ä¢ √âl√©ments architecturaux structur√©s\n\n### üí° Recommandations\n‚Ä¢ Projets urbains\n‚Ä¢ Design architectural\n‚Ä¢ Usage professionnel\n‚Ä¢ Impression et affichage num√©rique`,
        
        ar: `## üñºÔ∏è ÿßŸÑÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑÿ¥ÿßŸÖŸÑ ŸÑÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ±ÿ©\n\n### üìã ŸÖŸÑÿÆÿµ ÿ™ŸÜŸÅŸäÿ∞Ÿä\n**ÿßŸÑŸàÿµŸÅ ÿ®ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä:** ŸÖÿØŸäŸÜÿ© ÿ≠ÿ∂ÿ±Ÿäÿ© ŸÖÿπ ŸÖÿ®ÿßŸÜŸä ÿ≠ÿØŸäÿ´ÿ© Ÿàÿ≥ŸÖÿßÿ° ÿ≤ÿ±ŸÇÿßÿ°\n\n### üîç ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ\n**ÿßŸÑŸÅÿ¶ÿßÿ™ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©:**\n‚Ä¢ ŸÖÿ®ŸÜŸâ (ÿ´ŸÇÿ©: 92.3%)\n‚Ä¢ ŸÖÿØŸäŸÜÿ© (ÿ´ŸÇÿ©: 85.7%)\n‚Ä¢ ÿ¥ÿßÿ±ÿπ (ÿ´ŸÇÿ©: 78.2%)\n\n**ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ®ÿµÿ±Ÿä:**\n‚Ä¢ ÿßŸÑÿØŸÇÿ© : 1200 √ó 600 ÿ®ŸÉÿ≥ŸÑ\n‚Ä¢ ŸÜÿ≥ÿ®ÿ© ÿßŸÑÿ£ÿ®ÿπÿßÿØ : 2.00:1\n‚Ä¢ ÿßŸÑÿ£ŸÑŸàÿßŸÜ ÿßŸÑŸÖÿ≥Ÿäÿ∑ÿ±ÿ© : #42318, #16113, #76884e\n‚Ä¢ ŸÜŸàÿπ ÿßŸÑÿ™ÿ±ŸÉŸäÿ® : ÿ™ÿ±ŸÉŸäÿ® ÿ£ŸÅŸÇŸä ŸÖÿπ ÿÆÿ∑Ÿàÿ∑ ŸÇŸàŸäÿ©\n‚Ä¢ ŸÖÿ≥ÿ™ŸàŸâ ÿßŸÑÿ≥ÿ∑Ÿàÿπ : ŸÖÿ™Ÿàÿ≥ÿ∑\n\n### üé® ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑŸÅŸÜŸä\n‚Ä¢ ÿ™ÿ®ÿßŸäŸÜ ÿπÿßŸÑŸä Ÿäÿπÿ≤ÿ≤ ÿßŸÑŸàÿ∂Ÿàÿ≠\n‚Ä¢ ÿ™ŸÅÿßÿµŸäŸÑ ÿ≠ÿßÿØÿ© Ÿàÿ≠ŸàÿßŸÅ Ÿàÿßÿ∂ÿ≠ÿ©\n‚Ä¢ ÿπŸÜÿßÿµÿ± ŸÖÿπŸÖÿßÿ±Ÿäÿ© ÿ®ÿ™ÿµŸÖŸäŸÖ ŸÖŸÜÿ∏ŸÖ\n\n### üí° ÿßŸÑÿ™ŸàÿµŸäÿßÿ™\n‚Ä¢ ŸÖÿ¥ÿßÿ±Ÿäÿπ ÿπŸÖÿ±ÿßŸÜŸäÿ©\n‚Ä¢ ÿ™ÿµŸÖŸäŸÖ ŸÖÿπŸÖÿßÿ±Ÿä\n‚Ä¢ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ŸÖŸáŸÜŸä\n‚Ä¢ ÿ∑ÿ®ÿßÿπÿ© Ÿàÿπÿ±ÿ∂ ÿ±ŸÇŸÖŸä`,
        
        en: `## üñºÔ∏è COMPREHENSIVE IMAGE ANALYSIS REPORT\n\n### üìã Executive Summary\n**AI Description:** Urban city with modern buildings and blue sky\n\n### üîç Content Analysis\n**Main Categories:**\n‚Ä¢ Building (confidence: 92.3%)\n‚Ä¢ City (confidence: 85.7%)\n‚Ä¢ Street (confidence: 78.2%)\n\n**Visual Analysis:**\n‚Ä¢ Resolution: 1200 √ó 600 pixels\n‚Ä¢ Aspect Ratio: 2.00:1\n‚Ä¢ Dominant Colors: #42318, #16113, #76884e\n‚Ä¢ Composition Type: Horizontal composition with strong lines\n‚Ä¢ Brightness Level: Medium\n\n### üé® Technical Analysis\n‚Ä¢ High contrast enhances clarity\n‚Ä¢ Sharp details and defined edges\n‚Ä¢ Structured architectural elements\n\n### üí° Recommendations\n‚Ä¢ Urban projects\n‚Ä¢ Architectural design\n‚Ä¢ Professional use\n‚Ä¢ Print and digital display`
      };

      setDescription(mockDescriptions[selectedLanguage]);
      setAnalysisDetails({
        confidence: 0.92,
        dominant_colors: ['#42318', '#16113', '#76884e'],
        language: selectedLanguage
      });
      setIsAnalyzing(false);
    }, 3000);
  };

  const resetAnalysis = () => {
    setSelectedImage(null);
    setPreviewUrl('');
    setDescription('');
    setAnalysisDetails(null);
    setError('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const copyToClipboard = () => {
    if (description) {
      navigator.clipboard.writeText(description)
        .then(() => {
          alert(selectedLanguage === 'ar' ? 'ÿ™ŸÖ ŸÜÿ≥ÿÆ ÿßŸÑÿ™ŸÇÿ±Ÿäÿ±!' : 
                selectedLanguage === 'fr' ? 'Rapport copi√©!' : 'Report copied!');
        })
        .catch(err => {
          console.error('Erreur copie:', err);
        });
    }
  };

  return (
    <div className="image-description-container">
      <div className="description-wrapper">
        
        {/* En-t√™te avec s√©lecteur de langue */}
        <div className="description-header">
          <div className="header-content">
            <span className="header-icon">üîç</span>
            <h1>
              {selectedLanguage === 'ar' ? 'ŸÖÿ≠ŸÑŸÑ ÿßŸÑÿµŸàÿ± ÿ®ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä' :
               selectedLanguage === 'fr' ? 'Analyseur d\'Images IA' : 
               'AI Image Analyzer'}
            </h1>
          </div>
          <p className="header-subtitle">
            {selectedLanguage === 'ar' ? 'ÿ™ÿ≠ŸÑŸäŸÑ ŸÖÿ™ŸÇÿØŸÖ ‚Ä¢ ÿØÿπŸÖÂ§ö ÿßŸÑŸÑÿ∫ÿßÿ™ ‚Ä¢ ŸÜÿ™ÿßÿ¶ÿ¨ ÿØŸÇŸäŸÇÿ©' :
             selectedLanguage === 'fr' ? 'Analyse avanc√©e ‚Ä¢ Multi-langues ‚Ä¢ R√©sultats pr√©cis' :
             'Advanced analysis ‚Ä¢ Multi-language ‚Ä¢ Accurate results'}
          </p>

          {/* S√©lecteur de langue */}
          <div className="language-selector">
            {languages.map(lang => (
              <button
                key={lang.code}
                className={`lang-btn ${selectedLanguage === lang.code ? 'active' : ''}`}
                onClick={() => setSelectedLanguage(lang.code)}
              >
                <span className="flag">{lang.flag}</span>
                {lang.name}
              </button>
            ))}
          </div>
        </div>

        <div className="description-content">
          
          {/* Zone d'upload */}
          <div className="upload-section">
            <h3 className="section-title">
              {selectedLanguage === 'ar' ? 'ÿßÿ≥ÿ™Ÿäÿ±ÿßÿØ ÿßŸÑÿµŸàÿ±ÿ©' :
               selectedLanguage === 'fr' ? 'Importation de l\'Image' : 
               'Image Import'}
            </h3>
            
            <div className="upload-zone">
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleImageSelect}
                accept=".jpg,.jpeg,.png,.webp,.bmp,.gif"
                className="file-input"
              />
              
              <div className="upload-content">
                <span className="upload-icon">üìÅ</span>
                <div className="upload-text">
                  <h4>
                    {selectedLanguage === 'ar' ? 'ÿßÿ≥ÿ≠ÿ® Ÿàÿ£ŸÅŸÑÿ™ ÿµŸàÿ±ÿ™ŸÉ ŸáŸÜÿß' :
                     selectedLanguage === 'fr' ? 'Glissez-d√©posez votre image ici' : 
                     'Drag & drop your image here'}
                  </h4>
                  <p>
                    {selectedLanguage === 'ar' ? 'ÿ£Ÿà ÿßŸÜŸÇÿ± ŸÑŸÑÿ™ÿµŸÅÿ≠' :
                     selectedLanguage === 'fr' ? 'ou cliquez pour parcourir' : 
                     'or click to browse'}
                  </p>
                </div>
                <div className="format-info">
                  {selectedLanguage === 'ar' ? 'ÿßŸÑÿµŸäÿ∫ ÿßŸÑŸÖÿØÿπŸàŸÖÿ©: jpg, jpeg, png, webp, bmp, gif ‚Ä¢ ÿßŸÑÿ≠ÿØ ÿßŸÑÿ£ŸÇÿµŸâ 10MB' :
                   selectedLanguage === 'fr' ? 'Formats support√©s: jpg, jpeg, png, webp, bmp, gif ‚Ä¢ Max 10MB' :
                   'Supported formats: jpg, jpeg, png, webp, bmp, gif ‚Ä¢ Max 10MB'}
                </div>
              </div>
            </div>

            {previewUrl && (
              <div className="preview-section">
                <h4>
                  {selectedLanguage === 'ar' ? 'ŸÖÿπÿßŸäŸÜÿ© ÿßŸÑÿµŸàÿ±ÿ©' :
                   selectedLanguage === 'fr' ? 'Aper√ßu de l\'image' : 
                   'Image Preview'}
                </h4>
                <div className="image-preview">
                  <img src={previewUrl} alt="Preview" className="preview-image" />
                  <div className="preview-overlay">
                    <button onClick={resetAnalysis} className="remove-btn">
                      ‚úï
                    </button>
                  </div>
                </div>
                <div className="image-info">
                  <span>
                    {selectedLanguage === 'ar' ? 'ÿßŸÑŸÖŸÑŸÅ:' :
                     selectedLanguage === 'fr' ? 'Fichier:' : 'File:'} {selectedImage?.name}
                  </span>
                  <span>
                    {selectedLanguage === 'ar' ? 'ÿßŸÑÿ≠ÿ¨ŸÖ:' :
                     selectedLanguage === 'fr' ? 'Taille:' : 'Size:'} {(selectedImage?.size / 1024 / 1024).toFixed(2)} MB
                  </span>
                </div>
              </div>
            )}
          </div>

          {/* Bouton d'analyse */}
          {previewUrl && (
            <div className="analysis-section">
              <button
                onClick={analyzeImage}
                disabled={isAnalyzing}
                className="analyze-btn"
              >
                {isAnalyzing ? (
                  <>
                    <span className="spinner"></span>
                    {selectedLanguage === 'ar' ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ...' :
                     selectedLanguage === 'fr' ? 'Analyse en cours...' : 
                     'Analyzing...'}
                  </>
                ) : (
                  <>
                    <span className="analyze-icon">ü§ñ</span>
                    {selectedLanguage === 'ar' ? 'ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ±ÿ© ÿ®ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä' :
                     selectedLanguage === 'fr' ? 'Analyser l\'Image avec l\'IA' : 
                     'Analyze Image with AI'}
                  </>
                )}
              </button>
            </div>
          )}

          {/* Messages d'erreur */}
          {error && (
            <div className="error-box">
              <span className="error-icon">‚ö†Ô∏è</span>
              <div>
                <p><strong>
                  {selectedLanguage === 'ar' ? 'ÿÆÿ∑ÿ£:' :
                   selectedLanguage === 'fr' ? 'Erreur:' : 'Error:'}
                </strong> {error}</p>
              </div>
            </div>
          )}

          {/* R√©sultats */}
          {description && (
            <div className="results-section">
              <h3 className="results-title">
                {selectedLanguage === 'ar' ? 'üìã ÿßŸÑÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑŸÖŸèŸÜÿ¥ÿ¶' :
                 selectedLanguage === 'fr' ? 'üìã Rapport G√©n√©r√©' : 
                 'üìã Generated Report'}
              </h3>

              <div className="description-box">
                <div className="description-header">
                  <h4>
                    {selectedLanguage === 'ar' ? 'ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ™ŸÅÿµŸäŸÑŸä' :
                     selectedLanguage === 'fr' ? 'Analyse D√©taill√©e' : 
                     'Detailed Analysis'}
                  </h4>
                  <button onClick={copyToClipboard} className="copy-btn">
                    {selectedLanguage === 'ar' ? 'üìã ŸÜÿ≥ÿÆ' :
                     selectedLanguage === 'fr' ? 'üìã Copier' : 
                     'üìã Copy'}
                  </button>
                </div>
                <div className="description-content">
                  <pre className="analysis-text">{description}</pre>
                </div>
              </div>

              <div className="actions-section">
                <button onClick={resetAnalysis} className="reset-btn">
                  {selectedLanguage === 'ar' ? 'üîÑ ÿ™ÿ≠ŸÑŸäŸÑ ÿµŸàÿ±ÿ© ÿ£ÿÆÿ±Ÿâ' :
                   selectedLanguage === 'fr' ? 'üîÑ Analyser une Autre Image' : 
                   'üîÑ Analyze Another Image'}
                </button>
              </div>
            </div>
          )}

        </div>
      </div>
    </div>
  );
};

export default ImageDescriptionGenerator;
