import React, { useState } from 'react';
import './MultiAIImageGenerator.css';

const MultiAIImageGenerator = () => {
  const [prompt, setPrompt] = useState('');
  const [imageType, setImageType] = useState('logo');
  const [enhancedPrompt, setEnhancedPrompt] = useState('');
  const [images, setImages] = useState([]);
  const [isEnhancing, setIsEnhancing] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);
  const [error, setError] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);
  const [progress, setProgress] = useState(0);

  const examples = {
    logo: "Logo moderne et √©pur√© pour une marque de caf√© premium",
    character: "Personnage de bande dessin√©e r√©aliste, un aventurier dans la jungle",
    scene: "Paysage urbain futuriste au coucher du soleil, architecture cyberpunk",
    design: "Motif g√©om√©trique complexe inspir√© de l'art islamique, couleurs bleu et or"
  };

  const typeIcons = {
    logo: 'üéØ',
    character: 'üë®‚Äçüé®',
    scene: 'üèôÔ∏è',
    design: '‚ú®'
  };

  const typeLabels = {
    logo: 'Logo',
    character: 'Personnage',
    scene: 'Sc√®ne',
    design: 'Design'
  };

  const typeDescriptions = {
    logo: 'Designs de marque et identit√© visuelle',
    character: 'Personnages et cr√©atures uniques',
    scene: 'Paysages et environnements immersifs',
    design: 'Motifs et compositions artistiques'
  };

  // Am√©lioration du prompt avec Claude API
  const enhancePrompt = async () => {
    if (!prompt.trim()) {
      setError('Veuillez saisir une description pour votre image');
      return;
    }

    if (prompt.length > 500) {
      setError('La description ne peut pas d√©passer 500 caract√®res');
      return;
    }

    setIsEnhancing(true);
    setError('');
    setProgress(0);

    try {
      // Simulation de progression
      const progressInterval = setInterval(() => {
        setProgress(prev => {
          if (prev >= 90) {
            clearInterval(progressInterval);
            return 90;
          }
          return prev + 10;
        });
      }, 200);

      const response = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "x-api-key": process.env.REACT_APP_CLAUDE_API_KEY,
        },
        body: JSON.stringify({
          model: "claude-3-sonnet-20240229",
          max_tokens: 300,
          messages: [{
            role: "user",
            content: `En tant qu'expert en cr√©ation de prompts pour IA, transformez cette description simple en un prompt d√©taill√© et professionnel en anglais, optimis√© pour la g√©n√©ration d'images IA.

Type d'Image: ${imageType}
Description Utilisateur: ${prompt}

Exigences:
- Maximum 100 mots
- Soyez sp√©cifique sur le style, couleurs, √©clairage, composition
- Utilisez une terminologie artistique professionnelle
- Concentrez-vous sur les d√©tails visuels
- Sortez UNIQUEMENT le prompt am√©lior√©, sans explications ni guillemets

Prompt Am√©lior√©:`
          }]
        })
      });

      if (!response.ok) {
        throw new Error(`Erreur API: ${response.status}`);
      }

      const data = await response.json();
      const enhanced = data.content[0].text.trim();
      
      clearInterval(progressInterval);
      setProgress(100);
      
      setTimeout(() => {
        setEnhancedPrompt(enhanced);
        setIsEnhancing(false);
        setProgress(0);
        generateImages(enhanced);
      }, 500);

    } catch (err) {
      setError('Impossible d\'am√©liorer le texte. Utilisation de la version originale.');
      console.error('Erreur am√©lioration:', err);
      setIsEnhancing(false);
      setProgress(0);
      generateImages(prompt);
    }
  };

  // G√©n√©ration depuis diff√©rentes sources
  const generateImages = async (finalPrompt) => {
    setIsGenerating(true);
    setImages([]);
    setSelectedImage(null);
    setError('');

    const seed = Date.now();
    
    const typeConfigs = {
      logo: {
        prefix: 'professional logo design, modern, clean, ',
        styles: ['minimalist', 'vector', 'professional branding']
      },
      character: {
        prefix: 'character illustration, detailed, ',
        styles: ['concept art', 'digital painting', 'character design sheet']
      },
      scene: {
        prefix: 'photorealistic scene, cinematic, ',
        styles: ['epic landscape', 'atmospheric lighting', 'high detail']
      },
      design: {
        prefix: 'artistic design, intricate, ',
        styles: ['geometric pattern', 'ornamental', 'abstract art']
      }
    };

    const config = typeConfigs[imageType] || { prefix: '', styles: [] };

    const sources = [
      {
        name: 'Moteur Standard',
        description: '√âquilibre qualit√© et rapidit√©',
        url: `https://image.pollinations.ai/prompt/${encodeURIComponent(config.prefix + finalPrompt)}?width=512&height=512&seed=${seed}&enhance=true`,
        color: '#667eea',
        model: 'default'
      },
      {
        name: 'Moteur Avanc√©',
        description: 'Qualit√© sup√©rieure, plus de d√©tails',
        url: `https://image.pollinations.ai/prompt/${encodeURIComponent(config.prefix + finalPrompt + ', ' + config.styles[0] + ', high quality, 4k, detailed')}?width=512&height=512&seed=${seed + 1}&model=flux`,
        color: '#764ba2',
        model: 'flux'
      },
      {
        name: 'Moteur Artistique',
        description: 'Style cr√©atif et unique',
        url: `https://image.pollinations.ai/prompt/${encodeURIComponent(config.prefix + finalPrompt + ', ' + config.styles[1] + ', artistic style, vibrant colors, creative')}?width=512&height=512&seed=${seed + 2}&nologo=true`,
        color: '#f093fb',
        model: 'creative'
      }
    ];

    const generatedImages = [];
    
    for (let i = 0; i < sources.length; i++) {
      try {
        // Simulation de d√©lai progressif
        await new Promise(resolve => setTimeout(resolve, 1000 + i * 500));
        
        // V√©rification que l'URL est valide
        const imageUrl = sources[i].url;
        generatedImages.push({
          ...sources[i],
          status: 'loaded',
          id: i,
          url: imageUrl,
          timestamp: new Date().toLocaleTimeString()
        });
        
        setImages([...generatedImages]);
        
      } catch (err) {
        console.error(`Erreur g√©n√©ration image ${i}:`, err);
        generatedImages.push({
          ...sources[i],
          status: 'error',
          id: i,
          url: '',
          error: '√âchec de g√©n√©ration'
        });
      }
    }

    setIsGenerating(false);
  };

  const downloadImage = async (imageUrl, imageName) => {
    if (!imageUrl) {
      setError('URL d\'image invalide');
      return;
    }

    try {
      setError('');
      const response = await fetch(imageUrl);
      
      if (!response.ok) {
        throw new Error('Erreur de t√©l√©chargement');
      }
      
      const blob = await response.blob();
      
      // V√©rification de la taille
      if (blob.size < 1000) {
        throw new Error('Image corrompue ou trop petite');
      }
      
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `${imageName}-${Date.now()}.png`;
      link.style.display = 'none';
      
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      
      window.URL.revokeObjectURL(url);
      
    } catch (err) {
      setError('√âchec du t√©l√©chargement de l\'image. Veuillez r√©essayer.');
      console.error('Erreur t√©l√©chargement:', err);
    }
  };

  const downloadSelected = () => {
    if (selectedImage) {
      downloadImage(selectedImage.url, selectedImage.name);
    } else {
      setError('Veuillez s√©lectionner une image √† t√©l√©charger');
    }
  };

  const regenerateImages = () => {
    if (enhancedPrompt || prompt) {
      generateImages(enhancedPrompt || prompt);
    }
  };

  return (
    <div className="multi-ai-container">
      <div className="multi-ai-wrapper">
        
        {/* En-t√™te Professionnel */}
        <div className="multi-ai-header">
          <div className="header-content">
            <span className="header-icon">‚ú®</span>
            <h1>G√©n√©rateur d'Images IA Pro</h1>
          </div>
          <p className="header-subtitle">
            Technologie IA avanc√©e ‚Ä¢ G√©n√©ration multi-moteurs ‚Ä¢ R√©sultats professionnels
          </p>
        </div>

        <div className="multi-ai-content">
          
          {/* S√©lection du Type d'Image */}
          <div className="section">
            <h3 className="section-title">Type de Cr√©ation</h3>
            <div className="type-grid">
              {Object.keys(examples).map(type => (
                <button
                  key={type}
                  onClick={() => setImageType(type)}
                  className={`type-card ${imageType === type ? 'active' : ''}`}
                >
                  <span className="type-icon">{typeIcons[type]}</span>
                  <div className="type-text">
                    <span className="type-label">{typeLabels[type]}</span>
                    <span className="type-description">{typeDescriptions[type]}</span>
                  </div>
                </button>
              ))}
            </div>
          </div>

          {/* Saisie du Prompt */}
          <div className="section">
            <div className="input-header">
              <h3 className="section-title">Description de Votre Image</h3>
              <button
                onClick={() => {
                  setPrompt(examples[imageType]);
                  setError('');
                }}
                className="example-btn"
                type="button"
              >
                <span>üí°</span>
                Utiliser l'Exemple
              </button>
            </div>
            <textarea
              value={prompt}
              onChange={(e) => {
                setPrompt(e.target.value);
                setError('');
              }}
              placeholder={`D√©crivez en d√©tail l'image que vous souhaitez cr√©er... Exemple: "${examples[imageType]}"`}
              className="prompt-textarea"
              rows="4"
              maxLength="500"
            />
            <div className="char-counter">
              {prompt.length} / 500 caract√®res ‚Ä¢ 
              {prompt.length < 50 ? ' ‚ùå Trop court' : 
               prompt.length < 150 ? ' ‚ö†Ô∏è Peut √™tre plus d√©taill√©' : 
               ' ‚úÖ Description optimale'}
            </div>
          </div>

          {/* Bouton de G√©n√©ration avec Barre de Progression */}
          <div className="section">
            <button
              onClick={enhancePrompt}
              disabled={isEnhancing || isGenerating || !prompt.trim()}
              className="generate-btn"
              type="button"
            >
              {isEnhancing ? (
                <>
                  <span className="spinner"></span>
                  Optimisation IA en cours... {progress}%
                </>
              ) : isGenerating ? (
                <>
                  <span className="spinner"></span>
                  G√©n√©ration multi-moteurs...
                </>
              ) : (
                <>
                  <span className="wand-icon">ü™Ñ</span>
                  G√©n√©rer avec l'IA Avanc√©e
                </>
              )}
            </button>
            
            {isEnhancing && (
              <div className="progress-bar">
                <div 
                  className="progress-fill"
                  style={{ width: `${progress}%` }}
                ></div>
              </div>
            )}
          </div>

          {/* Affichage du Prompt Optimis√© */}
          {enhancedPrompt && (
            <div className="enhanced-box">
              <div className="enhanced-header">
                <span className="check-icon">‚úì</span>
                <h4>Prompt Optimis√© par l'IA</h4>
              </div>
              <p className="enhanced-text">{enhancedPrompt}</p>
              <div className="enhanced-info">
                <small>‚úÖ Description professionnelle g√©n√©r√©e ‚Ä¢ üéØ Optimis√©e pour {typeLabels[imageType]}</small>
              </div>
            </div>
          )}

          {/* Messages d'Erreur */}
          {error && (
            <div className="error-box">
              <span className="error-icon">‚ö†Ô∏è</span>
              <div>
                <p><strong>Attention:</strong> {error}</p>
                <small>Si le probl√®me persiste, actualisez la page et r√©essayez.</small>
              </div>
            </div>
          )}

          {/* Galerie des R√©sultats */}
          {images.length > 0 && (
            <div className="results-section">
              <h3 className="results-title">
                üé® R√©sultats de la G√©n√©ration Multi-Moteurs
              </h3>
              
              <div className="images-grid">
                {images.map((img) => (
                  <div
                    key={img.id}
                    className={`image-card ${selectedImage?.id === img.id ? 'selected' : ''}`}
                    onClick={() => setSelectedImage(img)}
                  >
                    <div 
                      className="color-bar"
                      style={{ backgroundColor: img.color }}
                    />
                    <div className="card-content">
                      <div className="card-header">
                        <div>
                          <h4 className="card-title">{img.name}</h4>
                          <p className="card-description">{img.description}</p>
                          <small className="model-info">Mod√®le: {img.model} ‚Ä¢ {img.timestamp}</small>
                        </div>
                        {selectedImage?.id === img.id && (
                          <span className="selected-badge">‚úì</span>
                        )}
                      </div>
                      
                      <div className="image-wrapper">
                        {img.status === 'loaded' ? (
                          <img
                            src={img.url}
                            alt={`G√©n√©r√© par ${img.name}`}
                            className="generated-img"
                            loading="lazy"
                            onError={(e) => {
                              e.target.style.display = 'none';
                              e.target.nextSibling.style.display = 'block';
                            }}
                          />
                        ) : (
                          <div className="image-error">
                            ‚ùå √âchec de chargement
                          </div>
                        )}
                      </div>
                      
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          downloadImage(img.url, img.name);
                        }}
                        className="download-btn-small"
                        disabled={img.status !== 'loaded'}
                      >
                        ‚¨áÔ∏è T√©l√©charger
                      </button>
                    </div>
                  </div>
                ))}
              </div>

              {/* Actions sur la S√©lection */}
              {selectedImage && (
                <div className="selected-box">
                  <p className="selected-text">
                    <strong>Image s√©lectionn√©e:</strong> {selectedImage.name} 
                    <br />
                    <small>Qualit√© {selectedImage.model} ‚Ä¢ Pr√™t au t√©l√©chargement</small>
                  </p>
