import React, { useState } from 'react';
import './ImageGenerator.css';

const ImageGenerator = () => {
  const [prompt, setPrompt] = useState('');
  const [imageType, setImageType] = useState('logo');
  const [imageUrl, setImageUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  // ÿ£ŸÖÿ´ŸÑÿ© ÿ¨ÿßŸáÿ≤ÿ© ŸÑŸÉŸÑ ŸÜŸàÿπ
  const examples = {
    logo: "ÿ¥ÿπÿßÿ± ÿπÿµÿ±Ÿä ŸÑÿ™ÿ∑ÿ®ŸäŸÇ ÿ¨ŸàÿßŸÑ ŸÖÿπ ÿ£ŸÑŸàÿßŸÜ ÿ≤ÿ±ŸÇÿßÿ° Ÿàÿ®Ÿäÿ∂ÿßÿ°",
    character: "ÿ¥ÿÆÿµŸäÿ© ŸÉÿ±ÿ™ŸàŸÜŸäÿ© ŸÑŸàŸÑÿØ ŸÖÿ∫ÿ±ÿ®Ÿä Ÿäÿ±ÿ™ÿØŸä ŸÇŸÅÿ∑ÿßŸÜ ÿ™ŸÇŸÑŸäÿØŸä",
    scene: "ŸÖŸÜÿ∏ÿ± ÿ∑ÿ®ŸäÿπŸä ŸÑÿ¨ÿ®ÿßŸÑ ÿßŸÑÿ£ÿ∑ŸÑÿ≥ ŸÖÿπ ÿ∫ÿ±Ÿàÿ® ÿßŸÑÿ¥ŸÖÿ≥",
    design: "ÿ™ÿµŸÖŸäŸÖ ŸÅŸÜŸä ÿ™ÿ¨ÿ±ŸäÿØŸä ÿ®ÿ£ŸÑŸàÿßŸÜ ÿØÿßŸÅÿ¶ÿ© ŸàŸÖÿ™ŸÜÿßÿ∫ŸÖÿ©"
  };

  const generateImage = () => {
    if (!prompt.trim()) {
      setError('ÿßŸÑÿ±ÿ¨ÿßÿ° ŸÉÿ™ÿßÿ®ÿ© ŸàÿµŸÅ ŸÑŸÑÿµŸàÿ±ÿ©');
      return;
    }

    setIsLoading(true);
    setError('');
    setImageUrl('');

    // ÿ™ÿ≠ÿ≥ŸäŸÜ ÿßŸÑŸÜÿµ ÿ≠ÿ≥ÿ® ÿßŸÑŸÜŸàÿπ
    let enhancedPrompt = prompt;
    switch (imageType) {
      case 'logo':
        enhancedPrompt = "professional logo design: " + prompt;
        break;
      case 'character':
        enhancedPrompt = "character illustration: " + prompt;
        break;
      case 'scene':
        enhancedPrompt = "photorealistic scene: " + prompt;
        break;
      case 'design':
        enhancedPrompt = "creative artistic design: " + prompt;
        break;
      default:
        enhancedPrompt = prompt;
    }

    // ÿ®ŸÜÿßÿ° ÿ±ÿßÿ®ÿ∑ ÿßŸÑÿµŸàÿ±ÿ©
    const url = `https://image.pollinations.ai/prompt/${encodeURIComponent(enhancedPrompt)}?width=512&height=512&seed=${Date.now()}`;
    
    // ŸÖÿ≠ÿßŸÉÿßÿ© ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ŸÑŸÖÿ¥ÿßŸáÿØÿ© ÿßŸÑŸÖÿ§ÿ¥ÿ±
    setTimeout(() => {
      setImageUrl(url);
      setIsLoading(false);
    }, 2000);
  };

  const downloadImage = async () => {
    try {
      const response = await fetch(imageUrl);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `ai-image-${Date.now()}.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (err) {
      setError('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸàÿ±ÿ©');
    }
  };

  const useExample = () => {
    setPrompt(examples[imageType]);
    setError('');
  };

  return (
    <div className="generator-container">
      {/* ÿßŸÑÿ±ÿ£ÿ≥ */}
      <div className="header">
        <h1>üé® ŸÖŸàŸÑŸëÿØ ÿßŸÑÿµŸàÿ± ÿ®ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä</h1>
        <p>ÿ≠ŸàŸëŸÑ ÿ£ŸÅŸÉÿßÿ±ŸÉ ÿ•ŸÑŸâ ÿµŸàÿ± ÿ±ÿßÿ¶ÿπÿ© - ŸÖÿ¨ÿßŸÜÿßŸã!</p>
      </div>

      {/* ÿßÿÆÿ™Ÿäÿßÿ± ŸÜŸàÿπ ÿßŸÑÿµŸàÿ±ÿ© */}
      <div className="type-selector">
        <h3>ÿßÿÆÿ™ÿ± ŸÜŸàÿπ ÿßŸÑÿµŸàÿ±ÿ©:</h3>
        <div className="type-buttons">
          {['logo', 'character', 'scene', 'design'].map((type) => (
            <button
              key={type}
              className={`type-btn ${imageType === type ? 'active' : ''}`}
              onClick={() => setImageType(type)}
            >
              {type === 'logo' && 'üéØ ŸÑŸàÿ¨Ÿà'}
              {type === 'character' && 'üë§ ÿ¥ÿÆÿµŸäÿ©'}
              {type === 'scene' && 'üåÜ ŸÖÿ¥ŸáÿØ'}
              {type === 'design' && '‚ú® ÿ™ÿµŸÖŸäŸÖ'}
            </button>
          ))}
        </div>
      </div>

      {/* ÿ≠ŸÇŸÑ ÿßŸÑÿ•ÿØÿÆÿßŸÑ */}
      <div className="input-section">
        <div className="input-header">
          <h3>ÿßŸÉÿ™ÿ® ŸàÿµŸÅ ÿßŸÑÿµŸàÿ±ÿ©:</h3>
          <button className="example-btn" onClick={useExample}>
            üí° ŸÖÿ´ÿßŸÑ
          </button>
        </div>
        <textarea
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder={`ŸÖÿ´ÿßŸÑ: ${examples[imageType]}`}
          className="prompt-input"
          rows="4"
        />
        <div className="char-count">
          {prompt.length} / 1000 ÿ≠ÿ±ŸÅ
        </div>
      </div>

      {/* ÿ≤ÿ± ÿßŸÑÿ™ŸàŸÑŸäÿØ */}
      <button 
        className={`generate-btn ${isLoading ? 'loading' : ''}`}
        onClick={generateImage}
        disabled={isLoading || !prompt.trim()}
      >
        {isLoading ? (
          <>
            <div className="spinner"></div>
            ÿ¨ÿßÿ±Ÿä ÿ•ŸÜÿ¥ÿßÿ° ÿµŸàÿ±ÿ™ŸÉ...
          </>
        ) : (
          'üé® ÿ™ŸàŸÑŸäÿØ ÿßŸÑÿµŸàÿ±ÿ©'
        )}
      </button>

      {/* ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿÆÿ∑ÿ£ */}
      {error && <div className="error-message">{error}</div>}

      {/* ÿπÿ±ÿ∂ ÿßŸÑÿµŸàÿ±ÿ© */}
      {imageUrl && (
        <div className="image-result">
          <h3>‚ú® ÿßŸÑŸÜÿ™Ÿäÿ¨ÿ©:</h3>
          <div className="image-container">
            <img src={imageUrl} alt="Generated" className="generated-image" />
          </div>
          <button className="download-btn" onClick={downloadImage}>
            ‚¨áÔ∏è ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸàÿ±ÿ©
          </button>
        </div>
      )}
    </div>
  );
};

export default ImageGenerator;
