import React, { useState, useRef } from 'react';
import './ImageDescriptionGenerator.css';

const ImageDescriptionGenerator = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [description, setDescription] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [error, setError] = useState('');
  const [analysisDetails, setAnalysisDetails] = useState(null);
  const fileInputRef = useRef(null);

  // Types de fichiers accept√©s
  const acceptedFormats = ['.jpg', '.jpeg', '.png', '.webp', '.bmp', '.gif'];

  const handleImageSelect = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    // Validation du fichier
    if (!acceptedFormats.some(format => file.name.toLowerCase().endsWith(format))) {
      setError(`Format non support√©. Utilisez: ${acceptedFormats.join(', ')}`);
      return;
    }

    if (file.size > 10 * 1024 * 1024) { // 10MB max
      setError('L\'image ne doit pas d√©passer 10MB');
      return;
    }

    setError('');
    setSelectedImage(file);
    setDescription('');
    setAnalysisDetails(null);

    // Cr√©er l'URL de pr√©visualisation
    const reader = new FileReader();
    reader.onload = (e) => {
      setPreviewUrl(e.target.result);
    };
    reader.readAsDataURL(file);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    event.currentTarget.classList.add('drag-over');
  };

  const handleDragLeave = (event) => {
    event.preventDefault();
    event.currentTarget.classList.remove('drag-over');
  };

  const handleDrop = (event) => {
    event.preventDefault();
    event.currentTarget.classList.remove('drag-over');
    
    const files = event.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      const inputEvent = {
        target: { files: [file] }
      };
      handleImageSelect(inputEvent);
    }
  };

  const analyzeImage = async () => {
    if (!selectedImage) {
      setError('Veuillez s√©lectionner une image');
      return;
    }

    setIsAnalyzing(true);
    setError('');
    setDescription('');
    setAnalysisDetails(null);

    try {
      // Cr√©er un FormData pour l'envoi
      const formData = new FormData();
      formData.append('image', selectedImage);
      formData.append('analysis_type', 'detailed');
      formData.append('language', 'fr');

      // Appel √† l'API Python
      const response = await fetch('http://localhost:8000/api/analyze-image', {
        method: 'POST',
        body: formData,
        headers: {
          'Accept': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`Erreur serveur: ${response.status}`);
      }

      const data = await response.json();

      if (data.success) {
        setDescription(data.description);
        setAnalysisDetails(data.details || {});
      } else {
        throw new Error(data.error || 'Erreur lors de l\'analyse');
      }

    } catch (err) {
      console.error('Erreur analyse:', err);
      
      // Simulation en cas d'erreur de connexion
      if (err.message.includes('Failed to fetch')) {
        setError('Impossible de se connecter au serveur. V√©rifiez que le serveur Python est d√©marr√©.');
        // Simulation pour la d√©mo
        simulateAnalysis();
      } else {
        setError(`Erreur: ${err.message}`);
      }
    } finally {
      setIsAnalyzing(false);
    }
  };

  // Simulation d'analyse pour la d√©mo
  const simulateAnalysis = () => {
    setTimeout(() => {
      const mockDescription = `Cette image pr√©sente un paysage urbain captivant au coucher du soleil. On observe une architecture moderne avec des b√¢timents aux lignes √©pur√©es et de grandes baies vitr√©es refl√©tant les derni√®res lueurs du jour. La sc√®ne est baign√©e dans une lumi√®re chaude et dor√©e caract√©ristique de l'heure bleue.

√âl√©ments principaux :
‚Ä¢ Architecture contemporaine avec des immeubles de verre et d'acier
‚Ä¢ Ciel teint√© de nuances orange, rose et bleu fonc√©
‚Ä¢ R√©flexions sur les surfaces vitr√©es cr√©ant une sym√©trie naturelle
‚Ä¢ Ambiance calme et presque m√©lancolique
‚Ä¢ √âclairage urbain commen√ßant √† s'allumer

Style photographique : Composition soign√©e avec une attention particuli√®re aux jeux de lumi√®re et reflets. Profondeur de champ ma√Ætris√©e mettant en valeur les d√©tails architecturaux.`;

      setDescription(mockDescription);
      setAnalysisDetails({
        confidence: 0.92,
        dominant_colors: ['#FF6B35', '#004E89', '#1A535C'],
        estimated_time_period: 'Contemporain',
        style: 'Photographie urbaine moderne',
        mood: 'Calme et contemplatif'
      });
      setIsAnalyzing(false);
    }, 3000);
  };

  const resetAnalysis = () => {
    setSelectedImage(null);
    setPreviewUrl('');
    setDescription('');
    setAnalysisDetails(null);
    setError('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const copyToClipboard = () => {
    if (description) {
      navigator.clipboard.writeText(description)
        .then(() => {
          alert('Description copi√©e dans le presse-papier !');
        })
        .catch(err => {
          console.error('Erreur copie:', err);
        });
    }
  };

  return (
    <div className="image-description-container">
      <div className="description-wrapper">
        
        {/* En-t√™te */}
        <div className="description-header">
          <div className="header-content">
            <span className="header-icon">üîç</span>
            <h1>Analyse d'Image par IA</h1>
          </div>
          <p className="header-subtitle">
            Intelligence Artificielle Python ‚Ä¢ Analyse d√©taill√©e ‚Ä¢ Description automatique
          </p>
        </div>

        <div className="description-content">
          
          {/* Zone d'Upload */}
          <div className="upload-section">
            <h3 className="section-title">Importation de l'Image</h3>
            
            <div 
              className="upload-zone"
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
              onClick={() => fileInputRef.current?.click()}
            >
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleImageSelect}
                accept={acceptedFormats.join(',')}
                className="file-input"
              />
              
              <div className="upload-content">
                <span className="upload-icon">üìÅ</span>
                <div className="upload-text">
                  <h4>Glissez-d√©posez votre image ici</h4>
                  <p>ou cliquez pour parcourir</p>
                </div>
                <div className="format-info">
                  Formats support√©s: {acceptedFormats.join(', ')} ‚Ä¢ Max 10MB
                </div>
              </div>
            </div>

            {previewUrl && (
              <div className="preview-section">
                <h4>Aper√ßu de l'image</h4>
                <div className="image-preview">
                  <img src={previewUrl} alt="Aper√ßu" className="preview-image" />
                  <div className="preview-overlay">
                    <button 
                      onClick={(e) => {
                        e.stopPropagation();
                        resetAnalysis();
                      }}
                      className="remove-btn"
                    >
                      ‚úï
                    </button>
                  </div>
                </div>
                <div className="image-info">
                  <span>Fichier: {selectedImage?.name}</span>
                  <span>Taille: {(selectedImage?.size / 1024 / 1024).toFixed(2)} MB</span>
                </div>
              </div>
            )}
          </div>

          {/* Bouton d'Analyse */}
          {previewUrl && (
            <div className="analysis-section">
              <button
                onClick={analyzeImage}
                disabled={isAnalyzing}
                className="analyze-btn"
              >
                {isAnalyzing ? (
                  <>
                    <span className="spinner"></span>
                    Analyse en cours par l'IA...
                  </>
                ) : (
                  <>
                    <span className="analyze-icon">ü§ñ</span>
                    Analyser l'Image avec l'IA Python
                  </>
                )}
              </button>
            </div>
          )}

          {/* Messages d'Erreur */}
          {error && (
            <div className="error-box">
              <span className="error-icon">‚ö†Ô∏è</span>
              <div>
                <p><strong>Erreur:</strong> {error}</p>
              </div>
            </div>
          )}

          {/* R√©sultats de l'Analyse */}
          {description && (
            <div className="results-section">
              <h3 className="results-title">
                üìã Description G√©n√©r√©e par l'IA
              </h3>

              <div className="description-box">
                <div className="description-header">
                  <h4>Analyse D√©taill√©e</h4>
                  <button
                    onClick={copyToClipboard}
                    className="copy-btn"
                  >
                    üìã Copier
                  </button>
                </div>
                <div className="description-content">
                  {description.split('\n\n').map((paragraph, index) => (
                    <p key={index} className="description-paragraph">
                      {paragraph}
                    </p>
                  ))}
                </div>
              </div>

              {/* D√©tails Techniques */}
              {analysisDetails && (
                <div className="details-section">
                  <h4>üìä M√©triques d'Analyse</h4>
                  <div className="details-grid">
                    {analysisDetails.confidence && (
                      <div className="detail-card">
                        <span className="detail-label">Confiance IA</span>
                        <span className="detail-value">
                          {(analysisDetails.confidence * 100).toFixed(1)}%
                        </span>
                      </div>
                    )}
                    {analysisDetails.dominant_colors && (
                      <div className="detail-card">
                        <span className="detail-label">Couleurs Dominantes</span>
                        <div className="color-palette">
                          {analysisDetails.dominant_colors.map((color, index) => (
                            <div
                              key={index}
                              className="color-swatch"
                              style={{ backgroundColor: color }}
                              title={color}
                            />
                          ))}
                        </div>
                      </div>
                    )}
                    {analysisDetails.style && (
                      <div className="detail-card">
                        <span className="detail-label">Style</span>
                        <span className="detail-value">{analysisDetails.style}</span>
                      </div>
                    )}
                    {analysisDetails.mood && (
                      <div className="detail-card">
                        <span className="detail-label">Ambiance</span>
                        <span className="detail-value">{analysisDetails.mood}</span>
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Actions */}
              <div className="actions-section">
                <button
                  onClick={resetAnalysis}
                  className="reset-btn"
                >
                  üîÑ Analyser une Autre Image
                </button>
              </div>
            </div>
          )}

          {/* Guide d'Utilisation */}
          <div className="info-section">
            <h4>üéØ Comment Obtenir les Meilleurs R√©sultats</h4>
            <div className="tips-grid">
              <div className="tip-card">
                <span className="tip-icon">üñºÔ∏è</span>
                <h5>Qualit√© d'Image</h5>
                <p>Utilisez des images nettes et bien √©clair√©es pour une analyse optimale</p>
              </div>
              <div className="tip-card">
                <span className="tip-icon">üé®</span>
                <h5>Contenu Riche</h5>
                <p>Les images avec des d√©tails visuels complexes donnent de meilleures descriptions</p>
              </div>
              <div className="tip-card">
                <span className="tip-icon">‚ö°</span>
                <h5>Performance</h5>
                <p>L'analyse prend g√©n√©ralement 2-5 secondes selon la complexit√©</p>
              </div>
            </div>
          </div>

        </div>
      </div>
    </div>
  );
};

export default ImageDescriptionGenerator;
