import React, { useState } from 'react';
import './MultiAIImageGenerator.css';

const MultiAIImageGenerator = () => {
  const [prompt, setPrompt] = useState('');
  const [imageType, setImageType] = useState('logo');
  const [enhancedPrompt, setEnhancedPrompt] = useState('');
  const [images, setImages] = useState([]);
  const [isEnhancing, setIsEnhancing] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);
  const [error, setError] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);

  const examples = {
    logo: "Logo moderne pour une boutique de caf√© sp√©cialis√©",
    character: "Personnage cartoon d'un enfant jouant au ballon",
    scene: "Plage tropicale avec des palmiers au coucher du soleil",
    design: "Design g√©om√©trique islamique avec des couleurs dor√©es"
  };

  const typeIcons = {
    logo: 'üéØ',
    character: 'üë§',
    scene: 'üåÜ',
    design: '‚ú®'
  };

  const typeLabels = {
    logo: 'Logo',
    character: 'Personnage',
    scene: 'Sc√®ne',
    design: 'Design'
  };

  // Am√©liorer le prompt avec Claude API
  const enhancePrompt = async () => {
    if (!prompt.trim()) {
      setError('Veuillez d\'abord √©crire une description de l\'image');
      return;
    }

    setIsEnhancing(true);
    setError('');

    try {
      const response = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          model: "claude-sonnet-4-20250514",
          max_tokens: 300,
          messages: [{
            role: "user",
            content: `You are an expert at writing image generation prompts. Convert this simple prompt into a detailed, professional English prompt optimized for AI image generation.

Image Type: ${imageType}
User Input: ${prompt}

Requirements:
- Keep it under 100 words
- Be specific about style, colors, lighting, composition
- Use professional art terminology
- Focus on visual details
- Output ONLY the enhanced prompt, no explanations or quotation marks

Enhanced Prompt:`
          }]
        })
      });

      const data = await response.json();
      const enhanced = data.content[0].text.trim();
      setEnhancedPrompt(enhanced);
      setIsEnhancing(false);
      
      // Commencer la g√©n√©ration automatiquement
      generateImages(enhanced);
    } catch (err) {
      setError('√âchec de l\'am√©lioration du texte. Nous utiliserons le texte original.');
      setIsEnhancing(false);
      generateImages(prompt);
    }
  };

  // G√©n√©rer depuis 3 sources diff√©rentes
  const generateImages = async (finalPrompt) => {
    setIsGenerating(true);
    setImages([]);
    setSelectedImage(null);

    const seed = Date.now();
    
    let typePrefix = '';
    switch (imageType) {
      case 'logo':
        typePrefix = 'professional logo design, ';
        break;
      case 'character':
        typePrefix = 'character illustration, ';
        break;
      case 'scene':
        typePrefix = 'photorealistic scene, ';
        break;
      case 'design':
        typePrefix = 'artistic design, ';
        break;
      default:
        typePrefix = '';
    }

    const fullPrompt = typePrefix + finalPrompt;

    const sources = [
      {
        name: 'Pollinations AI',
        description: 'Rapide et √©quilibr√©',
        url: `https://image.pollinations.ai/prompt/${encodeURIComponent(fullPrompt)}?width=512&height=512&seed=${seed}&enhance=true`,
        color: '#667eea'
      },
      {
        name: 'Pollinations V2',
        description: 'Version am√©lior√©e',
        url: `https://image.pollinations.ai/prompt/${encodeURIComponent(fullPrompt + ', high quality, detailed')}?width=512&height=512&seed=${seed + 1}&model=flux`,
        color: '#764ba2'
