        url: `https://image.pollinations.ai/prompt/${encodeURIComponent(fullPrompt + ', artistic style, vibrant colors')}?width=512&height=512&seed=${seed + 2}&nologo=true`,
        color: '#f093fb'
      }
    ];

    for (let i = 0; i < sources.length; i++) {
      await new Promise(resolve => setTimeout(resolve, 800));
      setImages(prev => [...prev, {
        ...sources[i],
        status: 'ready',
        id: i
