    }

    setIsGenerating(false);
  };

  const downloadImage = async (imageUrl, imageName) => {
    try {
      const response = await fetch(imageUrl);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `${imageName}-${Date.now()}.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (err) {
      setError('√âchec du t√©l√©chargement de l\'image');
    }
  };

  const downloadSelected = () => {
    if (selectedImage) {
      downloadImage(selectedImage.url, selectedImage.name);
    }
  };

  return (
    <div className="multi-ai-container">
      <div className="multi-ai-wrapper">
        
        {/* Header */}
        <div className="multi-ai-header">
          <div className="header-content">
            <span className="header-icon">‚ú®</span>
            <h1>G√©n√©rateur d'Images IA Avanc√©</h1>
          </div>
          <p className="header-subtitle">3 moteurs IA + am√©lioration intelligente = r√©sultats professionnels</p>
        </div>

        <div className="multi-ai-content">
          
          {/* Type d'image */}
          <div className="section">
            <h3 className="section-title">Choisissez le type d'image :</h3>
